        if(input$rpcs_GraphOptions_MPA == "With ONI"){
          return(0)
          
        }
        if(input$rpcs_GraphOptions_MPA == "With PDO (NOAA)"){
          return(1)
        }
        if(input$rpcs_GraphOptions_MPA == "With PDO (UW)"){
          return(0)
        }
