              geom_text(data = rpcs_Outside_MPA(),
                        aes(x = MPA_Date + 60, y = MPA_Mean, label = round(MPA_Mean, digits = 2)),
                        vjust = -.2, hjust = .5, angle = 0, alpha = as.numeric(input$rpcs_Bar_Text_MPA)) +
              geom_errorbar(data = rpcs_Outside_MPA(), 
