      NHSF_alphaPDO_UW_MPA <- reactive({
        if(input$NHSF_GraphOptions_MPA == "With No Index"){
          return(0)
        }
        if(input$NHSF_GraphOptions_MPA == "With ONI"){
          return(0)
          
        }
        if(input$NHSF_GraphOptions_MPA == "With PDO (NOAA)"){
          return(0)
        }
        if(input$NHSF_GraphOptions_MPA == "With PDO (UW)"){
          return(1)
