                    strip.text = element_text(size = 12, colour = "black", angle = 90, face = "bold")) 
          })
        } 
        else if(input$FSF_Graph_SD_MPA == "Violin Plot") { 
          return({   
            ggplot() +
              geom_rect(data = oni, aes(xmin= DateStart, xmax = DateEnd,ymin = 0, ymax = Inf, fill = ANOM), 
