        if(input$FSF_BarOptions_Isl == "stack"){
          y <- "Combined Densities"
        }
        else if(input$FSF_BarOptions_Isl == "dodge"){
          y <- "Seperated Densities"
        }
        else if(input$FSF_BarOptions_Isl == "fill"){
          y <- "Normalized Densities"
        }
        y
