          return(0)
          
        }
        if(input$FSF_GraphOptions_MPA == "With PDO (NOAA)"){
          return(1)
        }
        if(input$FSF_GraphOptions_MPA == "With PDO (UW)"){
          return(0)
        }
      }) # PDO NOAA layer toggle (changes alpha value)
      
